# coding: utf-8from __future__ import absolute_import, division, generators, nested_scopes, print_function, unicode_literals, with_statementimport sysimport reproc_file = open(sys.argv[1], 'r')lines = proc_file.readlines()proc_file.close()for i, line in enumerate(lines):	#print(line)	line = str(line)	try:		line.lstrip().startswith("*** Test Cases ***")		line.rstrip("\n").endswith("_change__")	except UnicodeDecodeError:		pass	else:		if line.lstrip().startswith("*** Test Cases ***"):			lines[i] = ""		if line.rstrip("\n").endswith("_change__"):			#print("wwwww")			#print(line)			#line = re.sub(r'\*\*\* *testcases *\*\*\*', '*** Processes ***', line, flags=re.I)			#line = "*** Test Cases *** #" + line + " _change__"			line = line.replace(" _change__", "").lstrip("#")			lines[i] = line			break			write_file = open(sys.argv[1], 'w')for line in lines:	#print(str(line))	write_file.write(str(line))write_file.close()