# coding: utf-8from __future__ import absolute_import, division, generators, nested_scopes, print_function, unicode_literals, with_statementimport sysimport reproc_file = open(sys.argv[1], 'r')lines = proc_file.readlines()proc_file.close()for i, line in enumerate(lines):	line = str(line)	print(line)	try:		line.replace(" ", "").lower()	except UnicodeDecodeError:		continue	if "***processes***" in line.replace(" ", "").lower():		#print("wwwww")		#print(line)		line = "*** Test Cases *** \n#" + line.rstrip("\n") + " _change__" + "\n"		lines[i] = line		breakwrite_file = open(sys.argv[1], 'w')for line in lines:	#print(line)	write_file.write(str(line))write_file.close()